mapper.sql:
SELECT alrtSub.ID AS ID, alrtSub.STATUS AS STATUS, MOBILE_PHONE, EMAIL_ID, LANG, NOTIFIED, CREATED_BY, DATE_CREATED, MODIFIED_BY, DATE_MODIFIED, DELETED_BY, DATE_DELETED, AUTHORIZED_BY, DATE_AUTHORIZED, DATE_UPDATED, alrtSub.COMP_CODE AS COMP_CODE, (SELECT SYS_PARAM_LOV_TRANS.VALUE_DESC FROM SYS_PARAM_LOV_TRANS WHERE SYS_PARAM_LOV_TRANS.VALUE_CODE = alrtSub.STATUS AND SYS_PARAM_LOV_TRANS.LOV_TYPE_ID = #{lovTypeId,jdbcType=NUMERIC} AND SYS_PARAM_LOV_TRANS.LANG_CODE = #{langCode,jdbcType=VARCHAR} ) STATUS_DESC, alrtSub.DEFAULT_BRIEF_NAME AS BRIEF_NAME, alrtSub.CIF_NO AS CIF_NO, alrtSub.USR_ID AS USR_ID, alrtSub.CHANNEL_END_USR_ID AS CHANNEL_END_USR_ID FROM ALRT_SUB alrtSub
INNER JOIN ALRT_SUB_GRP asg ON alrtSub.ID = asg.SUB_ID
, SYS_PARAM_LOV_LK_OPT lovOpt
WHERE 1=1
AND alrtSub.STATUS = lovOpt.VALUE_CODE AND lovOpt.LOV_TYPE_ID = #{lovTypeId,jdbcType=NUMERIC} AND lovOpt.PROG_REF = #{pageRef,jdbcType=VARCHAR} AND lovOpt.APP_NAME = #{currAppName,jdbcType=VARCHAR}
AND asg.GRP_ID = #{groupId}
AND alrtSub.COMP_CODE = #{compCode}

SELECT ALRT_SUB.ID, ALRT_SUB.CIF_NO, ALRT_SUB.USR_ID, ALRT_SUB.SUB_TYPE, ALRT_SUB.COMP_CODE, ALRT_SUB.BRANCH_CODE, ALRT_SUB.CURRENCY_CODE, ALRT_SUB.CIF_SUB_NO, ALRT_SUB.GL_CODE, ALRT_SUB.SL_NO, ALRT_SUB.STATUS, ALRT_SUB.DATE_UPDATED, (SELECT VALUE_DESC FROM SYS_PARAM_LOV_TRANS WHERE LOV_TYPE_ID = #{lovType,jdbcType=NUMERIC} AND LANG_CODE = #{langCode,jdbcType=VARCHAR} AND VALUE_CODE = CASE WHEN ALRT_SUB.STATUS IS NULL THEN 'N' ELSE ALRT_SUB.STATUS END) STATUS_DESC, ALRT_SUB.MOBILE_PHONE, ALRT_SUB.EMAIL_ID , ALRT_SUB.DEFAULT_BRIEF_NAME, ALRT_SUB.DEFAULT_MIDDLE_NAME, ALRT_SUB.DEFAULT_LONG_NAME, ALRT_SUB.DEFAULT_ADDRESS, ALRT_SUB.CHANNEL_END_USR_ID, ALRT_SUB.DATE_CREATED, ALRT_SUB.MODIFIED_BY, ALRT_SUB.DATE_MODIFIED, ALRT_SUB.AUTHORIZED_BY, ALRT_SUB.DATE_AUTHORIZED, ALRT_SUB.DELETED_BY, ALRT_SUB.DATE_DELETED, CHARGEABLE, FACEBOOK_SOCIAL_ID, TWITTER_SOCIAL_ID, NB_MSG, LANG, OLD_MOBILE_PHONE, SUSPEND_ALL_SUBSCRIPTIONS FROM ALRT_SUB
WHERE ID = #{subID,jdbcType=NUMERIC}
WHERE ALRT_SUB.CIF_NO = #{cif} AND ALRT_SUB.CHANNEL_END_USR_ID = #{userID}
WHERE ALRT_SUB.CIF_NO = #{cif}
WHERE ALRT_SUB.CHANNEL_END_USR_ID = #{userID}

mapper.select:
  		 	SELECT	COUNT(1)
			FROM		CIF
			WHERE	COMP_CODE	= #{compCode,jdbcType=NUMERIC}
			AND		CIF_NO		= #{cif,jdbcType=NUMERIC}

mapper.sql:
SELECT CIF.CIF_NO CIF_NO , CIF.SHORT_NAME_ENG SHORT_NAME_ENG , CIF.LONG_NAME_ENG LONG_NAME_ENG , CIF.ADDRESS1_ENG ADDRESS1_ENG , CIF_ADDRESS.EMAIL EMAIL , CIF_ADDRESS.MOBILE MOBILE , CIF.COMP_CODE COMP_CODE
FROM CIF LEFT OUTER JOIN CIF_ADDRESS ON ( CIF_ADDRESS.COMP_CODE = CIF.COMP_CODE AND CIF_ADDRESS.CIF_NO = CIF.CIF_NO AND CIF_ADDRESS.LINE_NO = 0) WHERE CIF.COMP_CODE = #{compCode,jdbcType=NUMERIC}
AND CIF.CIF_NO > 0 AND CIF.CIF_NO = #{cif,jdbcType=NUMERIC}

mapper.select:
SELECT COUNT(1)			
FROM  USR
WHERE USR.USER_ID =  #{userID,jdbcType=VARCHAR}

SELECT COUNT(*)
FROM AMF LEFT OUTER JOIN AMF_ADDRESS
   		 ON(AMF_ADDRESS.COMP_CODE= AMF.COMP_CODE
 and AMF_ADDRESS.ACC_BR= AMF.BRANCH_CODE
 and AMF_ADDRESS.ACC_CY = AMF.CURRENCY_CODE
 and AMF_ADDRESS.ACC_GL= AMF.GL_CODE
 and AMF_ADDRESS.ACC_CIF= AMF.CIF_SUB_NO
 and AMF_ADDRESS.ACC_SL = AMF.SL_NO  and AMF_ADDRESS.LINE_NO = 0)
	WHERE	 AMF.COMP_CODE 	= #{compCode,jdbcType=NUMERIC}
		AND AMF.BRANCH_CODE 	= #{accBr,jdbcType=NUMERIC}
		AND AMF.SL_NO 			= #{accSl,jdbcType=NUMERIC}
		AND AMF.CIF_SUB_NO 	    = #{accCif,jdbcType=NUMERIC}
		AND AMF.GL_CODE 		= #{accGl,jdbcType=NUMERIC}
		AND AMF.CURRENCY_CODE   = #{accCy,jdbcType=NUMERIC}
		AND (AMF.SL_NO NOT IN ( SELECT 	SL_NO FROM ALRT_SUB 
										WHERE 	ALRT_SUB.COMP_CODE 	    = #{compCode,jdbcType=NUMERIC}
											AND	ALRT_SUB.BRANCH_CODE    = #{accBr,jdbcType=NUMERIC}
											AND	ALRT_SUB.CURRENCY_CODE  = #{accCy,jdbcType=NUMERIC}
											AND	ALRT_SUB.GL_CODE		= #{accGl,jdbcType=NUMERIC}
											AND	ALRT_SUB.CIF_SUB_NO 	= #{accCif,jdbcType=NUMERIC}
											AND   ALRT_SUB.STATUS  <>  'D'
									 )
			 )
		AND AMF.STATUS IN ('A','I')

SELECT COUNT(*)
	FROM AMF LEFT OUTER JOIN AMF_ADDRESS
        ON(AMF_ADDRESS.COMP_CODE= AMF.COMP_CODE
		 and AMF_ADDRESS.ACC_BR= AMF.BRANCH_CODE
		 and AMF_ADDRESS.ACC_CY = AMF.CURRENCY_CODE
		 and AMF_ADDRESS.ACC_GL= AMF.GL_CODE
		 and AMF_ADDRESS.ACC_CIF= AMF.CIF_SUB_NO
		 and AMF_ADDRESS.ACC_SL = AMF.SL_NO  and AMF_ADDRESS.LINE_NO = 0)
	WHERE	 AMF.COMP_CODE 	= #{compCode,jdbcType=NUMERIC}
		AND AMF.BRANCH_CODE = #{accBr,jdbcType=NUMERIC}
		AND AMF.SL_NO 		= #{accSl,jdbcType=NUMERIC}
		AND AMF.CIF_SUB_NO 	= #{accCif,jdbcType=NUMERIC}
		AND AMF.GL_CODE 	= #{accGl,jdbcType=NUMERIC}
		AND AMF.CURRENCY_CODE = #{accCy,jdbcType=NUMERIC}
		AND AMF.STATUS IN ('A','I')

SELECT ADDITIONAL_REFERENCE 
			
			FROM   AMF 
			WHERE  COMP_CODE =#{compCode,jdbcType=NUMERIC}
			AND    BRANCH_CODE =  #{accBr,jdbcType=NUMERIC}
			AND    CURRENCY_CODE = #{accCy,jdbcType=NUMERIC}
			AND    GL_CODE = #{accGl,jdbcType=NUMERIC}
			AND    CIF_SUB_NO =  #{accCif,jdbcType=NUMERIC}
			AND    SL_NO =  #{accSl,jdbcType=NUMERIC}

SELECT COUNT(*)
FROM   GEN_LEDGER
WHERE	 GEN_LEDGER.GL_CODE = #{accGl,jdbcType=NUMERIC}
AND GEN_LEDGER.COMP_CODE    = #{compCode,jdbcType=NUMERIC}

SELECT COUNT(*)
FROM CURRENCIES
WHERE CURRENCIES.CURRENCY_CODE = #{accCy,jdbcType=NUMERIC}
AND CURRENCIES.COMP_CODE = #{compCode,jdbcType=NUMERIC}

SELECT COUNT(*)
FROM   BRANCHES
WHERE  BRANCHES.BRANCH_CODE = #{accBr,jdbcType=NUMERIC}
AND BRANCHES.COMP_CODE      = #{compCode,jdbcType=NUMERIC}

SELECT (CASE	WHEN PREFERED_LANGUAGE = 'A' THEN	'A' ELSE	'E' END) 
FROM USR
WHERE USER_ID =  #{userID,jdbcType=VARCHAR}

mapper.delete:
DELETE FROM ALRT_SUB_LANG
	WHERE ALRT_SUB_ID = #{subscriberID} 
	
		AND LANG_CODE != #{langCode}
	
	
		AND LANG_CODE = #{indSubLangCode}

mapper.sql:
	SELECT
		BRANCH_CODE,
		CURRENCY_CODE,
   		GL_CODE,
   		CIF_SUB_NO,
   		SL_NO,
   		
   		(CASE WHEN ADDITIONAL_REFERENCE IS NULL THEN ' ' ELSE ADDITIONAL_REFERENCE END) ADDITIONAL_REFERENCE,
		
		(CASE WHEN IBAN_ACC_NO IS NULL THEN ' '  ELSE IBAN_ACC_NO END) IBAN_ACC_NO
   		
   		
   		FROM ALRT_SUB_ACC_EXCL
   		where COMP_CODE = #{compCode}
		AND ALRT_SUB_ID = #{subID,jdbcType=NUMERIC} 
		
			AND EVT_PKG_ID = #{evtID,jdbcType=NUMERIC} 
		
		
			AND EVT_PKG_ID = #{pckgID,jdbcType=NUMERIC} 
		
		
			AND REL_TYPE = #{relType}

mapper.delete:
DELETE FROM ALRT_SUB_ACC_EXCL
	WHERE COMP_CODE = #{compCode}
	AND ALRT_SUB_ID = #{subscriberID}

